mapper.select:
/* PortfolioMapper.categoryList */

    SELECT CATEGORY_ID
         , ID
         , RANDOM_ID
         , CATEGORY_NM
      FROM CATEGORY
     WHERE ID           = #{id}
       AND RANDOM_ID    = #{randomId}
     ORDER BY CATEGORY_ID

mapper.insert:
/* PortfolioMapper.insertCategory */

    INSERT INTO CATEGORY
         ( CATEGORY_ID
         , ID
         , RANDOM_ID
         , CATEGORY_NM)
    SELECT COALESCE(NULLIF(MAX(CATEGORY_ID), 0), 0) + 1
         , #{id}
         , #{randomId}
         , #{categoryNm}
      FROM CATEGORY
     WHERE ID = #{id}
       AND RANDOM_ID = #{randomId}
     ORDER BY MAX(CATEGORY_ID) DESC

mapper.update:
/* PortfolioMapper.updateCategory */

    UPDATE CATEGORY
       SET CATEGORY_NM	= #{categoryNm}
     WHERE CATEGORY_ID	= #{categoryId}
       AND ID			= #{id}
       AND RANDOM_ID	= #{randomId}

mapper.delete:
/* PortfolioMapper.deleteCategory */

    DELETE FROM CATEGORY
     WHERE CATEGORY_ID	= #{categoryId}
       AND ID			= #{id}
       AND RANDOM_ID	= #{randomId}

mapper.select:
/* PortfolioMapper.selectCategoryCnt */

    SELECT COUNT(CATEGORY_ID)
      FROM PORTFOLIO
     WHERE ID           = #{id}
       AND RANDOM_ID    = #{randomId}
       AND CATEGORY_ID  = #{categoryId}

/* PortfolioMapper.portfolioAllList */
SELECT A.ID , A.RANDOM_ID , A.PORT_ID , A.TITLE , A.CONTENT , A.CATEGORY_ID , COALESCE(NULLIF(C.category_nm, ''), '') AS category_nm , a.MD_USE_YN , A.IMG_FILE_ID , A.START_YMD , A.END_YMD , B.FILE AS IMG_FILE FROM PORTFOLIO A LEFT OUTER JOIN category C ON A.category_id = C.category_id AND A.ID = C.id AND A.RANDOM_ID = C.RANDOM_ID LEFT OUTER JOIN FILE B ON A.IMG_FILE_ID = B.FILE_ID WHERE A.ID = #{id} AND A.RANDOM_ID = #{randomId}
AND A.CATEGORY_ID = #{categoryId}
ORDER BY START_YMD DESC, PORT_ID

/* PortfolioMapper.selectMaxPortId */

    SELECT COALESCE(NULLIF(MAX(PORT_ID), 0), 0) + 1
      FROM PORTFOLIO
     WHERE ID 			= #{id}
       AND RANDOM_ID 	= #{randomId}

mapper.insert:
/* PortfolioMapper.insertPortfolio */

    INSERT INTO PORTFOLIO
         ( ID
         , RANDOM_ID
         , PORT_ID
         , TITLE
         , CONTENT
         , CATEGORY_ID
         , IMG_FILE_ID
         , START_YMD
         , END_YMD
         , MD_USE_YN
         , MD_DATA
         )
    VALUES
         ( #{id}
         , #{randomId}
         , #{portId}
         , #{title}
         , #{content}
         , #{categoryId}
         , #{imgFileId}
         , #{startYmd}
         , #{endYmd}
         , #{mdUseYn}
         , #{mdData}
         )

/* PortfolioMapper.insertPortfolioDetail */

    INSERT INTO PORTFOLIO_DETAIL
         ( DETAIL_ID
         , ID
         , RANDOM_ID
         , PORT_ID
         , DETAIL_SEQ
         , DETAIL_DIVISION
         , DETAIL_TITLE
         , DETAIL_CONTENT
         )
    SELECT COALESCE(NULLIF(MAX(DETAIL_ID), 0), 0) + 1
         , #{id}
         , #{randomId}
         , #{portId}
         , COALESCE(NULLIF(MAX(DETAIL_SEQ), 0), 0) + 1
         , #{detailDivision}
         , #{detailTitle}
         , #{detailContent}
      FROM PORTFOLIO_DETAIL
     WHERE ID	 		= #{id}
       AND RANDOM_ID 	= #{randomId}
       AND PORT_ID 		= #{portId}

mapper.select:
/* PortfolioMapper.selectMaxSkillId */

    SELECT COALESCE(NULLIF(MAX(SKILL_ID), 0), 0) + 1
      FROM PORTFOLIO_SKILL
     WHERE ID 			= #{id}
       AND RANDOM_ID 	= #{randomId}
       AND PORT_ID      = #{portId}

mapper.insert:
/* PortfolioMapper.insertPortSkill */

    INSERT INTO PORTFOLIO_SKILL
         ( ID
         , RANDOM_ID
         , PORT_ID
         , SKILL_ID
         , SKILL_TITLE
         )
    VALUES
         ( #{id}
         , #{randomId}
         , #{portId}
         , #{skillId}
         , #{skillTitle}
         )

/* PortfolioMapper.insertPortSkillDetail */

    INSERT INTO PORTFOLIO_SKILL_DETAIL
         ( ID
         , RANDOM_ID
         , PORT_ID
         , SKILL_ID
         , SKILL_IMG_ID
         , SKILL_URI
         , SKILL_IMG_TITLE
         , SKILL_HEX
         )
    VALUES
         ( #{id}
         , #{randomId}
         , #{portId}
         , #{skillId}
         , #{skillImgId}
         , #{skillUri}
         , #{skillImgTitle}
         , #{skillHex}
         )

mapper.select:
/* PortfolioMapper.portfolioDetailList */

    SELECT DETAIL_ID
         , ID
         , RANDOM_ID
         , PORT_ID
         , DETAIL_SEQ
         , DETAIL_DIVISION
         , DETAIL_TITLE
         , DETAIL_CONTENT
      FROM PORTFOLIO_DETAIL
     WHERE ID 			= #{id}
       AND RANDOM_ID 	= #{randomId}
       AND PORT_ID 		= #{portId}

/* PortfolioMapper.selectPortSkillId */

    SELECT SKILL_ID
      FROM PORTFOLIO_SKILL
     WHERE ID           = #{id}
       AND RANDOM_ID    = #{randomId}
       AND PORT_ID      = #{portId}

/* PortfolioMapper.portSkillDetailList */

    SELECT SKILL_IMG_ID
         , SKILL_ID
         , SKILL_URI
         , SKILL_IMG_TITLE
         , SKILL_HEX
      FROM PORTFOLIO_SKILL_DETAIL
     WHERE ID           = #{id}
       AND RANDOM_ID    = #{randomId}
       AND PORT_ID      = #{portId}
       AND SKILL_ID     = #{skillId}

mapper.update:
/* PortfolioMapper.updatePortfolio */

    UPDATE PORTFOLIO
       SET TITLE        = #{title}
         , CONTENT      = #{content}
         , CATEGORY_ID  = #{categoryId}
         , IMG_FILE_ID  = #{imgFileId}
         , MD_USE_YN    = #{mdUseYn}
         , MD_DATA      = #{mdData}
         , START_YMD    = #{startYmd}
         , END_YMD      = #{endYmd}
     WHERE ID           = #{id}
       AND RANDOM_ID    = #{randomId}
       AND PORT_ID      = #{portId}

mapper.delete:
/* PortfolioMapper.deletePortfolio */

    DELETE FROM PORTFOLIO
     WHERE ID 			= #{id}
       AND RANDOM_ID 	= #{randomId}
       AND PORT_ID 		= #{portId}

/* PortfolioMapper.deletePortfolioDetailAll */

    DELETE FROM PORTFOLIO_DETAIL
     WHERE ID 			= #{id}
       AND RANDOM_ID	= #{randomId}
       AND PORT_ID 		= #{portId}

/* PortfolioMapper.deletePortSkill */

    DELETE FROM PORTFOLIO_SKILL
     WHERE ID           = #{id}
       AND RANDOM_ID    = #{randomId}
       AND PORT_ID      = #{portId}
       AND SKILL_ID     = #{skillId}

/* PortfolioMapper.deletePortSkillDetailAll */

    DELETE FROM PORTFOLIO_SKILL_DETAIL
     WHERE ID           = #{id}
       AND RANDOM_ID    = #{randomId}
       AND PORT_ID      = #{portId}
       AND SKILL_ID     = #{skillId}

mapper.update:
/* PortfolioMapper.updatePortfolioImg */

    UPDATE PORTFOLIO
       SET IMG_FILE_ID  = #{imgFileId}
     WHERE ID           = #{id}
       AND RANDOM_ID    = #{randomId}
       AND PORT_ID      = #{portId}

/* PortfolioMapper.updatePortAttachFile */

    UPDATE PORTFOLIO
       SET FILE_ID      = #{fileId}
     WHERE ID           = #{id}
       AND RANDOM_ID    = #{randomId}
       AND PORT_ID      = #{portId}

/* PortfolioMapper.updateFileUseYn */

    UPDATE FILE
       SET USE_YN   = 'N'
     WHERE FILE_ID  = #{imgFileId}

/* PortfolioMapper.updatePortFileUseYn */

    UPDATE FILE
       SET USE_YN   = 'N'
     WHERE FILE_ID  = (SELECT IMG_FILE_ID
                         FROM PORTFOLIO
                        WHERE ID        = #{id}
                          AND RANDOM_ID = #{randomId}
                          AND PORT_ID   = #{portId}
                      )

mapper.select:
/* PortfolioMapper.getAttachFileCnt */

    SELECT COUNT(FILE_ID)
      FROM FILE
     WHERE FILE_ID = #{fileId}

mapper.update:
/* PortfolioMapper.updateAttachFileToNull */

    UPDATE PORTFOLIO
       SET FILE_ID = NULL
     WHERE ID           = #{id}
       AND RANDOM_ID    = #{randomId}
       AND PORT_ID      = #{portId}
