mapper.insert:

INSERT INTO CATEGORY
( CATEGORY_ID
, ID
, RANDOM_ID
, CATEGORY_NM)
SELECT COALESCE(NULLIF(MAX(CATEGORY_ID), 0), 0) + 1
     , #{id}
     , #{randomId}
     , #{categoryNm}
FROM CATEGORY
WHERE ID = #{id}
  AND RANDOM_ID = #{randomId}
ORDER BY MAX(CATEGORY_ID) DESC

mapper.update:

UPDATE CATEGORY
SET CATEGORY_NM	= #{categoryNm}
WHERE CATEGORY_ID	= #{categoryId}
  AND ID			= #{id}
  AND RANDOM_ID	= #{randomId}

mapper.delete:

DELETE FROM CATEGORY
 WHERE CATEGORY_ID	= #{categoryId}
   AND ID			= #{id}
   AND RANDOM_ID	= #{randomId}
