mapper.insert:

INSERT INTO
    RESUME_LICENSE(  LICENSE_ID
                  , ID
                  , RANDOM_ID
                  , LICENSE_NM
                  , LICENSE_ISSUE_ORG
                  , LICENSE_PASS_YN
                  , LICENSE_PASS_DATE
                  , SEQ)
SELECT  COALESCE(NULLIF(MAX(LICENSE_ID), 0), 0) + 1
     ,#{id}
     ,#{random_id}
     ,#{license_nm}
     ,#{license_issue_org}
     ,#{license_pass_yn}
     ,#{license_pass_date}
     ,#{seq}
FROM RESUME_LICENSE
WHERE ID = #{id}
  and RANDOM_ID = #{randomId}


INSERT INTO
    RESUME_LANG_TEST(  LANG_TEST_ID
                    , ID
                    , RANDOM_ID
                    , LANG_NM
                    , LANG_TEST_NM
                    , LANG_ACQUIRE_DATE
                    , LANG_TEST_SCORE
                    , LANG_ACQUIRE_YN
                    , SEQ)
SELECT  COALESCE(NULLIF(MAX(LANG_TEST_ID), 0), 0) + 1
     ,#{id}
     ,#{randomId}
     ,#{langNm}
     ,#{langTestNm}
     ,#{langAcquireDate}
     ,#{langTestScore}
     ,#{langAcquireYn}
     ,#{seq}
FROM RESUME_LANG_TEST
WHERE ID = #{id}
  and RANDOM_ID = #{randomId}


INSERT INTO
    RESUME_AWARD(   AWARD_ID
                , ID
                , RANDOM_ID
                , AWARD_NM
                , AWARD_DATE
                , AWARD_ORG
                , SEQ)
SELECT  COALESCE(NULLIF(MAX(AWARD_ID), 0), 0) + 1
     ,#{id}
     ,#{randomId}
     ,#{awardNm}
     ,#{awardDate}
     ,#{awardOrg}
     ,#{seq}
FROM RESUME_AWARD
WHERE ID = #{id}
  and RANDOM_ID = #{randomId}

mapper.update:

update RESUME_LICENSE
set     LICENSE_NM = #{license_nm}
        ,LICENSE_iSSUE_ORG = #{license_issue_org}
        ,LICENSE_PASS_YN = #{license_pass_yn}
        ,LICENSE_PASS_DATE = #{license_pass_date}
where ID = #{id}
  and RANDOM_ID = #{randomId}
  and LICENSE_ID = #{licenseId}


update RESUME_LANG_TEST
set  LANG_NM = #{langNm}
  ,LANG_TEST_NM = #{langTestNm}
  ,LANG_ACQUIRE_DATE = #{langAcquireDate}
  ,LANG_TEST_SCORE = #{langTestScore}
  ,LANG_ACQUIRE_YN = #{langAcquireYn}
where ID = #{id}
  and RANDOM_ID = #{randomId}
  and LANG_TEST_ID = #{langTestId}


update RESUME_AWARD
set AWARD_NM = #{awardNm}
  , AWARD_DATE = #{awardDate}
  , AWARD_ORG= #{awardOrg}
where ID = #{id}
  and RANDOM_ID = #{randomId}
  and AWARD_ID = #{awardId}

mapper.delete:

DELETE FROM RESUME_LICENSE
WHERE ID = #{id}
  and RANDOM_ID = #{randomId}
  and LICENSE_ID = #{licenseId}


DELETE FROM RESUME_LANG_TEST
WHERE ID = #{id}
  and RANDOM_ID = #{randomId}
  and LANG_TEST_ID = #{langTestId}


DELETE FROM RESUME_AWARD
WHERE ID = #{id}
  and RANDOM_ID = #{randomId}
  and AWARD_ID = #{awardId}

mapper.update:

update RESUME_LICENSE
set  SEQ = #{seq}
where ID = #{id}
  and RANDOM_ID = #{randomId}
  and LICENSE_ID = #{licenseId}


update RESUME_LANG_TEST
set  SEQ = #{seq}
where ID = #{id}
  and RANDOM_ID = #{randomId}
  and LANG_TEST_ID = #{langTestId}


update RESUME_AWARD
set  SEQ = #{seq}
where ID = #{id}
  and RANDOM_ID = #{randomId}
  and AWARD_ID = #{awardId}
