mapper.select:

SELECT *
  FROM RESUME_EDU
 WHERE ID        = #{id}
   AND RANDOM_ID = #{randomId}
 ORDER BY SCHOOL_CD

mapper.insert:

INSERT INTO RESUME_EDU
     ( EDU_ID
     , ID
     , RANDOM_ID
     , SCHOOL_CD
     , SCHOOL_NM
     , GRADUATE_STATE
     , ENTRANCE_DATE
     , GRADUATE_DATE
     , MAJOR
     , MAJOR_AVR
     , SCHOOL_GB )
SELECT COALESCE(NULLIF(MAX(EDU_ID), 0), 0) + 1
     , #{id}
     , #{randomId}
     , #{schoolCd}
     , #{schoolNm}
     , #{graduateState}
     , #{entranceDate}
     , #{graduateDate}
     , #{major}
     , #{majorAvr}
     , #{schoolGb}
 FROM RESUME_EDU
WHERE ID        = #{id}
  AND RANDOM_ID = #{randomId}

mapper.update:

UPDATE RESUME_EDU
   SET SCHOOL_NM      = #{schoolNm}
     , GRADUATE_STATE = #{graduateState}
     , ENTRANCE_DATE  = #{entranceDate}
     , GRADUATE_DATE  = #{graduateDate}
     , MAJOR          = #{major}
     , MAJOR_AVR      = #{majorAvr}
     , SCHOOL_GB      = #{schoolGb}
 WHERE ID         = #{id}
   AND RANDOM_ID  = #{randomId}
   AND edu_id     = #{eduId}

mapper.delete:

DELETE FROM RESUME_EDU
 WHERE ID        = #{id}
   AND RANDOM_ID = #{randomId}
   AND edu_id    = #{eduId}
